func testMempoolBench(aNum, tNum int) {
	cfg := config.DefaultMempoolConfig()
	cfg.Lifetime = 3 * time.Hour
	statsReportInterval = 8 * time.Second
	evictionInterval = 1 * time.Minute

	app := testNewMockApp(aNum)
	//p2pNodeInfo := p2p.NodeInfo{}
	mem := NewMempool(cfg, 0, nil)
	//	mem.SetLogger(testGetLogger())
	mem.app = app

	txs := make([][]*types.Tx, aNum)
	to := app.accounts[0]
	for i := 0; i < aNum; i++ {
		txs[i] = make([]*types.Tx, tNum)

		from := app.accounts[i]
		for j := 0; j < tNum; j++ {
			tx, err := testGenEtx(from, to, uint64(j), big.NewInt(1))
			if err != nil {
				panic(err)
			}
			txs[i][j] = tx
		}
	}

	var wg sync.WaitGroup

	loopSum := aNum * tNum
	sTime := time.Now()
	for i := 0; i < aNum; i++ {
		wg.Add(1)
		go func(mtxs []*types.Tx) {
			for _, tx := range mtxs {
				mem.AddTx("", *tx)
			}
			wg.Done()
		}(txs[i])
	}
	wg.Wait()

	used := time.Now().Sub(sTime)
	nsop := float64(used.Nanoseconds()) / float64(loopSum)

	fmt.Printf("BenchTest used %s,  %.2f ns/op,  %.2f op/s\n", used, nsop, float64(time.Second.Nanoseconds())/nsop)

	specPending, pending, queue := mem.Stats()
	fmt.Println("specGood", specPending, "goodTxs:", pending, "futureTxs:", queue)

	mem.Stop()
}

func printGoodTxs(mem *Mempool) {
	fmt.Println("\n----goodTxs")
	for e := mem.goodTxs.Front(); e != nil; e = e.Next() {
		memTx := e.Value.(*mempoolTx)
		from, _ := memTx.tx.From()
		fmt.Println(from.Hex(), memTx.tx.TypeName(), memTx.tx.Hash().Hex())
	}
	fmt.Println("----goodTxs end")
}

func printSpecGoodTxs(mem *Mempool) {
	fmt.Println("\n----specgoodTxs")
	for e := mem.specGoodTxs.Front(); e != nil; e = e.Next() {
		memTx := e.Value.(*mempoolTx)
		switch tx := memTx.tx.(type) {
		case *types.MultiSignAccountTx:
			from, _ := tx.From()
			fmt.Printf("MultiSignAccountTx:%v from:%v\n", tx, from)
		default:
			panic("tx is not a MultiSignAccountTx or UpdateAddrRouteTx")
		}
	}
	fmt.Println("----specgoodTxs end")
}

func printFutureTxs(mem *Mempool) {
	fmt.Println("\n----futureTxs")
	for addr, m := range mem.futureTxs {
		fmt.Println("addr: ", addr.Hex())
		for _, nonce := range *m.txs.index {
			fmt.Println(addr.Hex(), nonce, m.txs.Get(nonce).Hash().Hex())
		}
	}
	fmt.Println("----futureTxs end")
}

func TestTxHeap(t *testing.T) {
	cache := newTxHeapManager(4, 3)
	nums := 32

	var hash common.Hash
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		cache.Put(makeNopTx(hash))
	}
	t.Logf("cache.Put ok")

	if cache.Size() != nums {
		t.Fatalf("cache.Size(): wanted=%d, goted=%d", nums, cache.Size())
	}

	hash = common.Hash{}
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := cache.Get(hash)
		if tx == nil {
			t.Fatalf("cache.Get should not be nil, i=%d", i)
		}
		t.Logf("cache.Get %s", tx.Hash().String())
		cache.DelayDelete(hash)
	}
	t.Logf("cache.Get ok")

	time.Sleep(time.Second * 4)
	hash = common.Hash{}
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := cache.Get(hash)
		if tx != nil {
			t.Fatalf("cache.Get should be nil, i=%d", i)
		}
	}
	if cache.Size() != 0 {
		t.Fatalf("cache.Size(): wanted=%d, goted=%d", 0, cache.Size())
	}
	t.Logf("cache.DelayDelete ok")
}

func TestTxHeapPlus(t *testing.T) {
	cache := newTxHeapManager(4, 3)
	nums := 32

	var hash common.Hash
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := &mempoolCachedTx{
			Tx:           makeNopTx(hash),
			BasicChecked: false,
		}
		cache.Put(tx)
		t.Log(cache.Get(hash), cache.CheckAndGet(hash))
		if i < nums/2 {
			tx.BasicChecked = true
		}
		t.Log(cache.Get(hash), cache.CheckAndGet(hash))
	}
	if cache.Size() != nums {
		t.Fatalf("cache.Size(): wanted=%d, goted=%d", nums, cache.Size())
	}
	hash = common.Hash{}
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := cache.CheckAndGet(hash)
		if tx == nil && i < nums/2 {
			t.Fatalf("cache.Get should not be nil, i=%d", i)
		}
		if tx != nil && i >= nums/2 {
			t.Fatalf("cache.Get should be nil, i=%d", i)
		}
	}
	t.Logf("cache.CheckAndGet ok")

	hash = common.Hash{}
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := cache.Get(hash)
		if tx == nil {
			t.Fatalf("cache.Get should not be nil, i=%d", i)
		}
		t.Logf("cache.Get %s", tx.Hash().String())
		cache.DelayDelete(hash)
	}
	t.Logf("cache.Get ok")

	time.Sleep(time.Second * 4)
	hash = common.Hash{}
	for i := 0; i < nums; i++ {
		hash[0] = byte(i)
		tx := cache.Get(hash)
		if tx != nil {
			t.Fatalf("cache.Get should be nil, i=%d", i)
		}
	}
	if cache.Size() != 0 {
		t.Fatalf("cache.Size(): wanted=%d, goted=%d", 0, cache.Size())
	}
	t.Logf("cache.DelayDelete ok")
}

func BenchmarkTxHeap(b *testing.B) {
	cache := newTxHeapManager(4, 3)

	var hash common.Hash
	for i := 0; i < b.N; i++ {
		hash[0] = byte(i % 0xff)
		hash[1] = byte(i / 0xff)
		cache.Put(makeNopTx(hash))
	}

	for i := 0; i < b.N; i++ {
		hash[0] = byte(i % 0xff)
		hash[1] = byte(i / 0xff)
		if tx := cache.Get(hash); tx == nil {
			b.Fatalf("cache.Get should be nil for %s", hash.String())
		}
	}
}
